sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
	initialize: function() {
					this.minProcessingDays = 0;
				},

	isNeededForDateValid: function(strDate, numDays) {
							// initialize the dates to compare
							var testdate = new GlideDateTime(strDate);
							var rightnow = new GlideDateTime();

							// make sure the testdate meets our criteria:  Must be numDays from today
							rightnow.addDaysUTC(numDays);
							if(testdate.getDate().before(rightnow.getDate())){
								return false;
							} else {
								return true;
							}
	},
    type: 'NeedItUtils'
};
